package ua.sumdu.group8.Gallery.dao;/** * This class contains constants representing Oracle SQL queries. * * @author Buzov Andrey * @version 1.0 * created 26-Apr-2010 */public class GallerySQLConstants {        public final static String ST_ADD_CAT = "INSERT INTO Catalogues(id, parent, name, descr) VALUES(NULL,?,?,?)";    public final static String ST_ADD_PIC = "INSERT INTO pictures(id, cat, name, url, descr) VALUES(NULL,?,?,?,?)";    public final static String ST_DEL_CAT = "DELETE FROM Catalogues WHERE id = ?";    public final static String ST_DEL_PIC = "DELETE FROM Pictures WHERE id = ?";    public final static String ST_UPD_CAT = "UPDATE Catalogues SET parent = ?, name = ?, descr = ? WHERE id = ?";    public final static String ST_UPD_PIC = "UPDATE Pictures SET cat = ?, name = ?, url = ?, descr = ? WHERE id = ?";    public final static String ST_GET_CAT_BY_ID = "SELECT DISTINCT id, parent, name, descr FROM Catalogues WHERE id = ?";    public final static String ST_GET_CAT_BY_NAME = "SELECT DISTINCT id, parent, name, descr FROM Catalogues WHERE name = ?";    public final static String ST_GET_CAT_BY_PARENT = "SELECT DISTINCT id, parent, name, descr FROM Catalogues WHERE parent = ?";    public final static String ST_GET_PIC_BY_ID = "SELECT DISTINCT id, cat, name, url, descr FROM Pictures WHERE id = ?";    public final static String ST_GET_PIC_BY_NAME = "SELECT DISTINCT id, cat, name, url, descr FROM Pictures WHERE name = ?";    public final static String ST_GET_PIC_BY_CAT = "SELECT DISTINCT id, cat, name, url, descr FROM Pictures WHERE cat = ?";    public final static String ST_GET_ROOT = "SELECT DISTINCT id, name, descr FROM Catalogues WHERE parent IS NULL";    public final static String ORDER_NAME_ASC = " ORDER BY name ASC";    public final static String ORDER_NAME_DESC = " ORDER BY name DESC";    public final static int SORT_ASC = 0;     public final static int SORT_DESC = 1;        public final static int ROOT = 0;}